contracts/ActivityTracker.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

interface IDepartmentRegistry {
    function superAdmins(address) external view returns (bool);
    function departmentHeads(address) external view returns (bool);
}

contract ActivityTracker {
    IDepartmentRegistry public departmentRegistry;

    struct Activity {
        uint256 id;
        address department;
        string activityType;
        uint256 amount;
        string description;
        uint256 timestamp;
        bytes32 txHash;
        string status;
    }

    mapping(address => Activity[]) public departmentActivities;
    mapping(address => uint256) public activityCount;
    uint256 public totalActivities;

    event ActivityLogged(
        uint256 indexed id,
        address indexed department,
        string activityType,
        uint256 amount,
        string description,
        bytes32 txHash,
        string status
    );

    constructor(address _departmentRegistry) {
        departmentRegistry = IDepartmentRegistry(_departmentRegistry);
    }

    modifier onlyAuthorized() {
        require(
            departmentRegistry.superAdmins(msg.sender) ||
            departmentRegistry.departmentHeads(msg.sender),
            "Not authorized"
        );
        _;
    }

    function logActivity(
        address department,
        string memory activityType,
        uint256 amount,
        string memory description,
        bytes32 txHash,
        string memory status
    ) external onlyAuthorized {
        totalActivities++;
        uint256 activityId = totalActivities;

        Activity memory newActivity = Activity({
            id: activityId,
            department: department,
            activityType: activityType,
            amount: amount,
            description: description,
            timestamp: block.timestamp,
            txHash: txHash,
            status: status
        });

        departmentActivities[department].push(newActivity);
        activityCount[department]++;

        emit ActivityLogged(
            activityId,
            department,
            activityType,
            amount,
            description,
            txHash,
            status
        );
    }

    function getDepartmentActivities(address department) 
        external 
        view 
        returns (Activity[] memory) 
    {
        return departmentActivities[department];
    }

    function getRecentActivities(address department, uint256 limit) 
        external 
        view 
        returns (Activity[] memory) 
    {
        uint256 count = activityCount[department];
        uint256 resultCount = limit < count ? limit : count;
        Activity[] memory result = new Activity[](resultCount);

        for (uint256 i = 0; i < resultCount; i++) {
            result[i] = departmentActivities[department][count - 1 - i];
        }

        return result;
    }
}

contracts/BudgetController.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "./DepartmentRegistry.sol";

// Import the Department struct
interface IDepartmentRegistry {
    struct Department {
        string name;
        uint256 budget;
        uint256 spent;
        uint256 efficiency;
        uint256 projects;
        bool isActive;
        address departmentHead;
    }

    function getDepartmentDetails(address departmentAddress) external view returns (Department memory);
    function departments(address) external view returns (Department memory);
    function departmentHeads(address) external view returns (bool);
    function superAdmins(address) external view returns (bool);
    function updateDepartmentMetrics(address, uint256, uint256, uint256) external;
    function isDepartmentActive(address department) external view returns (bool);
    function getDepartmentBudget(address department) external view returns (uint256);
    function updateDepartmentBudget(address department, uint256 newBudget) external;
    function getAllDepartments() external view returns (address[] memory);
}

contract BudgetController {
    IDepartmentRegistry public departmentRegistry;

    // Reentrancy guard
    bool private locked;
    modifier nonReentrant() {
        require(!locked, "Reentrant call");
        locked = true;
        _;
        locked = false;
    }

    enum TransactionType { 
        BUDGET_ALLOCATION,
        PROJECT_FUNDING,
        BUDGET_UPDATE,
        EXPENSE 
    }
    enum TransactionStatus { PENDING, COMPLETED, FAILED }

    struct Transaction {
        uint256 id;
        address department;
        TransactionType txType;
        uint256 amount;
        string description;
        TransactionStatus status;
        uint256 timestamp;
    }

    mapping(uint256 => Transaction) public transactions;
    uint256 public transactionCount;

    uint256 private constant MAX_TRANSACTION_AMOUNT = 100000 ether;
    mapping(address => uint256[]) public departmentTransactions;
    mapping(address => uint256) public departmentTransactionCount;

    event TransactionCreated(
        uint256 indexed id,
        address indexed department,
        TransactionType txType,
        uint256 amount
    );
    event TransactionCompleted(uint256 indexed id, TransactionStatus status);
    event BudgetLimitWarning(address indexed department, uint256 spent, uint256 budget);
    event BudgetUpdated(address indexed department, uint256 oldBudget, uint256 newBudget, uint256 transactionId);
    event TotalBudgetUpdated(uint256 newTotalBudget);
    event TotalSpentUpdated(uint256 newTotalSpent);

    uint256 public totalBudgetAllocated;
    uint256 public totalBudgetSpent;

    constructor(address _departmentRegistry) {
        departmentRegistry = IDepartmentRegistry(_departmentRegistry);
    }

    modifier onlyDepartmentHead() {
        require(departmentRegistry.departmentHeads(msg.sender), "Not department head");
        _;
    }

    modifier onlySuperAdmin() {
        require(departmentRegistry.superAdmins(msg.sender), "Not super admin");
        _;
    }

    modifier validateAmount(uint256 amount) {
        require(amount > 0 && amount <= MAX_TRANSACTION_AMOUNT, "Invalid amount");
        _;
    }

    function createTransaction(
        TransactionType txType,
        uint256 amount,
        string memory description
    ) public returns (uint256) {
        transactionCount++;
        uint256 transactionId = transactionCount;

        transactions[transactionId] = Transaction({
            id: transactionId,
            department: msg.sender,
            txType: txType,
            amount: amount,
            description: description,
            status: TransactionStatus.COMPLETED,
            timestamp: block.timestamp
        });

        departmentTransactions[msg.sender].push(transactionId);
        departmentTransactionCount[msg.sender]++;

        emit TransactionCreated(transactionId, msg.sender, txType, amount);
        emit TransactionCompleted(transactionId, TransactionStatus.COMPLETED);

        return transactionId;
    }

    function processTransaction(uint256 transactionId) 
        external 
        onlySuperAdmin 
        nonReentrant 
    {
        Transaction storage transaction = transactions[transactionId];
        require(transaction.id != 0, "Transaction not found");
        require(transaction.status == TransactionStatus.PENDING, "Transaction not pending");

        IDepartmentRegistry.Department memory dept = departmentRegistry.getDepartmentDetails(transaction.department);

        if (transaction.txType == TransactionType.EXPENSE) {
            require(dept.spent + transaction.amount <= dept.budget, "Budget exceeded");
            updateDepartmentBudget(transaction.department, transaction.txType, transaction.amount);
        } else if (transaction.txType == TransactionType.BUDGET_ALLOCATION) {
            require(dept.budget + transaction.amount <= MAX_TRANSACTION_AMOUNT, "Exceeds max allocation");
            updateDepartmentBudget(transaction.department, transaction.txType, transaction.amount);
        }

        transaction.status = TransactionStatus.COMPLETED;
        updateTotals();
        emit TransactionCompleted(transactionId, TransactionStatus.COMPLETED);
    }

    function updateDepartmentBudget(
        address department,
        TransactionType txType,
        uint256 amount
    ) internal {
        IDepartmentRegistry.Department memory dept = departmentRegistry.getDepartmentDetails(department);
        uint256 newSpent = dept.spent;
        uint256 newBudget = dept.budget;

        if (txType == TransactionType.EXPENSE) {
            newSpent += amount;
        } else if (txType == TransactionType.BUDGET_ALLOCATION) {
            newBudget += amount;
        }

        uint256 newEfficiency = calculateEfficiency(newSpent, newBudget);
        departmentRegistry.updateDepartmentMetrics(
            department,
            newEfficiency,
            dept.projects,
            newSpent
        );

        if (newSpent >= newBudget * 90 / 100) { // 90% threshold
            emit BudgetLimitWarning(department, newSpent, newBudget);
        }
    }

    function calculateEfficiency(uint256 spent, uint256 budget) 
        internal 
        pure 
        returns (uint256) 
    {
        if (budget == 0) return 0;
        return (spent * 100) / budget;
    }

    function getTransactionsByDepartment(address department)
        external
        view
        returns (Transaction[] memory)
    {
        uint256[] memory txIds = departmentTransactions[department];
        Transaction[] memory result = new Transaction[](txIds.length);

        for (uint256 i = 0; i < txIds.length; i++) {
            result[i] = transactions[txIds[i]];
        }

        return result;
    }

    function initializeDepartmentBudget(
        address department,
        uint256 initialBudget
    ) external onlySuperAdmin nonReentrant validateAmount(initialBudget) {
        require(
            departmentRegistry.departments(department).isActive,
            "Department not active"
        );

        transactionCount++;
        uint256 transactionId = transactionCount;

        transactions[transactionId] = Transaction({
            id: transactionId,
            department: department,
            txType: TransactionType.BUDGET_ALLOCATION,
            amount: initialBudget,
            description: "Initial budget allocation",
            status: TransactionStatus.COMPLETED,
            timestamp: block.timestamp
        });

        departmentTransactions[department].push(transactionId);
        departmentTransactionCount[department]++;

        emit TransactionCreated(transactionId, department, TransactionType.BUDGET_ALLOCATION, initialBudget);
        emit TransactionCompleted(transactionId, TransactionStatus.COMPLETED);
    }

    // Function to update department budget
    function updateDepartmentBudget(address department, uint256 newBudget) external onlySuperAdmin {
        require(departmentRegistry.isDepartmentActive(department), "Department is not active");
        uint256 currentBudget = departmentRegistry.getDepartmentBudget(department);

        // Create a budget update transaction
        uint256 transactionId = createTransaction(
            TransactionType.BUDGET_UPDATE,
            newBudget > currentBudget ? newBudget - currentBudget : currentBudget - newBudget,
            "Budget Update"
        );

        // Update the budget in DepartmentRegistry
        departmentRegistry.updateDepartmentBudget(department, newBudget);

        emit BudgetUpdated(department, currentBudget, newBudget, transactionId);
    }

    function submitTransaction(
        TransactionType txType,
        uint256 amount,
        string memory description
    ) external onlyDepartmentHead returns (uint256) {
        require(amount > 0 && amount <= MAX_TRANSACTION_AMOUNT, "Invalid amount");

        transactionCount++;
        uint256 transactionId = transactionCount;

        transactions[transactionId] = Transaction({
            id: transactionId,
            department: msg.sender,
            txType: txType,
            amount: amount,
            description: description,
            status: TransactionStatus.PENDING,
            timestamp: block.timestamp
        });

        departmentTransactions[msg.sender].push(transactionId);
        departmentTransactionCount[msg.sender]++;

        emit TransactionCreated(transactionId, msg.sender, txType, amount);

        return transactionId;
    }

    function updateTotals() public {
        uint256 newTotalBudget = 0;
        uint256 newTotalSpent = 0;

        address[] memory allDepartments = departmentRegistry.getAllDepartments();
        for(uint i = 0; i < allDepartments.length; i++) {
            IDepartmentRegistry.Department memory dept = departmentRegistry.getDepartmentDetails(allDepartments[i]);
            newTotalBudget += dept.budget;
            newTotalSpent += dept.spent;
        }

        totalBudgetAllocated = newTotalBudget;
        totalBudgetSpent = newTotalSpent;

        emit TotalBudgetUpdated(newTotalBudget);
        emit TotalSpentUpdated(newTotalSpent);
    }

    function getSystemMetrics() external view returns (
        uint256 totalBudget,
        uint256 totalSpent,
        uint256 pendingTransactionCount,
        uint256 completedTransactionCount
    ) {
        uint256 pending = 0;
        uint256 completed = 0;

        for(uint256 i = 1; i <= transactionCount; i++) {
            if(transactions[i].status == TransactionStatus.PENDING) {
                pending++;
            } else if(transactions[i].status == TransactionStatus.COMPLETED) {
                completed++;
            }
        }

        return (
            totalBudgetAllocated,
            totalBudgetSpent,
            pending,
            completed
        );
    }
}

contracts/DepartmentRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

contract DepartmentRegistry {
    // Role management
    mapping(address => bool) public superAdmins;
    mapping(address => bool) public departmentHeads;
    bool public paused;

    struct Department {
        string name;
        uint256 budget;
        uint256 spent;
        uint256 efficiency;
        uint256 projects;
        bool isActive;
        address departmentHead;
        string logoUri;
    }

    mapping(address => Department) public departments;
    address[] public departmentList;

    uint256 private constant MAX_BUDGET = 1000000 ether;
    mapping(address => uint256) public departmentBalances;
    mapping(address => uint256[]) public departmentEfficiencyHistory;

    event DepartmentRegistered(
        address indexed departmentAddress, 
        string name, 
        uint256 budget,
        address departmentHead
    );
    event BudgetUpdated(address indexed departmentAddress, uint256 newBudget);
    event DepartmentHeadChanged(address indexed departmentAddress, address indexed newHead);
    event MetricsUpdated(address indexed department, uint256 efficiency, uint256 spent);
    event SystemPaused(address indexed admin);
    event SystemUnpaused(address indexed admin);

    modifier onlySuperAdmin() {
        require(superAdmins[msg.sender], "Not super admin");
        _;
    }

    modifier onlyDepartmentHead() {
        require(departmentHeads[msg.sender], "Not department head");
        _;
    }

    modifier whenNotPaused() {
        require(!paused, "System is paused");
        _;
    }

    constructor() {
        superAdmins[msg.sender] = true;
        emit SystemPaused(msg.sender);
    }

    function registerDepartment(
        address departmentAddress,
        string memory name,
        uint256 initialBudget,
        address departmentHead,
        string memory logoUri
    ) external onlySuperAdmin whenNotPaused {
        require(!departments[departmentAddress].isActive, "Department already exists");
        require(departmentHead != address(0), "Invalid department head");
        require(bytes(name).length > 0, "Name cannot be empty");
        require(initialBudget <= MAX_BUDGET, "Budget exceeds maximum");

        departments[departmentAddress] = Department({
            name: name,
            budget: initialBudget,
            spent: 0,
            efficiency: 100,
            projects: 0,
            isActive: true,
            departmentHead: departmentHead,
            logoUri: logoUri
        });

        departmentList.push(departmentAddress);
        departmentHeads[departmentHead] = true;
        departmentBalances[departmentAddress] = initialBudget;

        emit DepartmentRegistered(
            departmentAddress, 
            name, 
            initialBudget,
            departmentHead
        );
    }

    function updateBudget(address departmentAddress, uint256 newBudget) 
        external 
        onlySuperAdmin 
    {
        require(departments[departmentAddress].isActive, "Department not found");
        departments[departmentAddress].budget = newBudget;
        emit BudgetUpdated(departmentAddress, newBudget);
    }

    function changeDepartmentHead(address departmentAddress, address newHead) 
        external 
        onlySuperAdmin 
    {
        require(departments[departmentAddress].isActive, "Department not found");
        address oldHead = departments[departmentAddress].departmentHead;

        departments[departmentAddress].departmentHead = newHead;
        departmentHeads[oldHead] = false;
        departmentHeads[newHead] = true;

        emit DepartmentHeadChanged(departmentAddress, newHead);
    }

    function getDepartmentDetails(address departmentAddress) 
        external 
        view 
        returns (Department memory) 
    {
        require(departments[departmentAddress].isActive, "Department not found");
        return departments[departmentAddress];
    }

    function updateDepartmentMetrics(
        address departmentAddress, 
        uint256 newEfficiency,
        uint256 projectCount,
        uint256 newSpent
    ) external onlySuperAdmin whenNotPaused {
        require(departments[departmentAddress].isActive, "Department not found");
        require(newSpent <= departments[departmentAddress].budget, "Exceeds budget");

        departments[departmentAddress].efficiency = newEfficiency;
        departments[departmentAddress].projects = projectCount;
        departments[departmentAddress].spent = newSpent;

        departmentEfficiencyHistory[departmentAddress].push(newEfficiency);

        emit MetricsUpdated(departmentAddress, newEfficiency, newSpent);
    }

    function getEfficiencyHistory(address departmentAddress) 
        external 
        view 
        returns (uint256[] memory) 
    {
        return departmentEfficiencyHistory[departmentAddress];
    }

    function getAllDepartments() external view returns (address[] memory) {
        return departmentList;
    }

    function pause() external onlySuperAdmin {
        paused = true;
        emit SystemPaused(msg.sender);
    }

    function unpause() external onlySuperAdmin {
        paused = false;
        emit SystemUnpaused(msg.sender);
    }

    function addSuperAdmin(address admin) external onlySuperAdmin {
        superAdmins[admin] = true;
    }

    function removeSuperAdmin(address admin) external onlySuperAdmin {
        require(msg.sender != admin, "Cannot remove self");
        superAdmins[admin] = false;
    }

    function deactivateDepartment(address departmentAddress) 
        external 
        onlySuperAdmin 
        whenNotPaused 
    {
        require(departments[departmentAddress].isActive, "Department not found");
        departments[departmentAddress].isActive = false;
        departmentHeads[departments[departmentAddress].departmentHead] = false;
    }

    function isSuperAdmin(address account) public view returns (bool) {
        return superAdmins[account];
    }
}

contracts/ProposalManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "./DepartmentRegistry.sol";

// Import the Department struct
interface IDepartmentRegistry {
    struct Department {
        string name;
        uint256 budget;
        uint256 spent;
        uint256 efficiency;
        uint256 projects;
        bool isActive;
        address departmentHead;
    }

    function getDepartmentDetails(address departmentAddress) external view returns (Department memory);
    function departments(address) external view returns (Department memory);
    function departmentHeads(address) external view returns (bool);
    function superAdmins(address) external view returns (bool);
}

contract ProposalManager {
    IDepartmentRegistry public departmentRegistry;

    // Reentrancy guard
    bool private locked;
    modifier nonReentrant() {
        require(!locked, "Reentrant call");
        locked = true;
        _;
        locked = false;
    }

    enum ProposalStatus { PENDING, UNDER_REVIEW, APPROVED, REJECTED }

    struct Proposal {
        uint256 id;
        address department;
        string title;
        uint256 amount;
        string description;
        ProposalStatus status;
        uint256 submittedDate;
        uint256 reviewedDate;
        address reviewedBy;
        string category;
    }

    struct ProposalComment {
        address commenter;
        string comment;
        uint256 timestamp;
    }

    mapping(uint256 => Proposal) public proposals;
    mapping(uint256 => ProposalComment[]) public proposalComments;
    mapping(address => uint256[]) public departmentProposals;
    uint256 public proposalCount;
    uint256 public totalProposals;
    uint256 public approvedProposals;
    uint256 public pendingProposals;

    event ProposalSubmitted(uint256 indexed id, address indexed department, uint256 amount);
    event ProposalReviewed(uint256 indexed id, ProposalStatus status, address reviewer);
    event CommentAdded(uint256 indexed proposalId, address indexed commenter, string comment);

    constructor(address _departmentRegistry) {
        departmentRegistry = IDepartmentRegistry(_departmentRegistry);
    }

    modifier onlyDepartmentHead() {
        require(departmentRegistry.departmentHeads(msg.sender), "Not department head");
        _;
    }

    modifier onlySuperAdmin() {
        require(departmentRegistry.superAdmins(msg.sender), "Not super admin");
        _;
    }

    modifier validateAmount(uint256 amount) {
        require(amount > 0, "Amount must be positive");
        _;
    }

    function submitProposal(
        string memory title,
        uint256 amount,
        string memory description,
        string memory category
    ) external onlyDepartmentHead nonReentrant validateAmount(amount) returns (uint256) {
        IDepartmentRegistry.Department memory dept = departmentRegistry.getDepartmentDetails(msg.sender);
        require(amount <= dept.budget - dept.spent, "Insufficient budget");

        proposalCount++;
        uint256 proposalId = proposalCount;

        proposals[proposalId] = Proposal({
            id: proposalId,
            department: msg.sender,
            title: title,
            amount: amount,
            description: description,
            status: ProposalStatus.PENDING,
            submittedDate: block.timestamp,
            reviewedDate: 0,
            reviewedBy: address(0),
            category: category
        });

        departmentProposals[msg.sender].push(proposalId);
        totalProposals++;
        pendingProposals++;
        emit ProposalSubmitted(proposalId, msg.sender, amount);
        return proposalId;
    }

    function reviewProposal(
        uint256 proposalId,
        ProposalStatus newStatus
    ) external onlySuperAdmin nonReentrant {
        Proposal storage proposal = proposals[proposalId];
        require(proposal.id != 0, "Proposal not found");
        require(proposal.status == ProposalStatus.PENDING, "Proposal not pending");

        proposal.status = newStatus;
        proposal.reviewedDate = block.timestamp;
        proposal.reviewedBy = msg.sender;

        if(newStatus == ProposalStatus.APPROVED) {
            approvedProposals++;
        }
        pendingProposals--;

        emit ProposalReviewed(proposalId, newStatus, msg.sender);
    }

    function getProposalsByDepartment(address department) 
        external 
        view 
        returns (Proposal[] memory) 
    {
        uint256[] memory proposalIds = departmentProposals[department];
        Proposal[] memory result = new Proposal[](proposalIds.length);

        for (uint256 i = 0; i < proposalIds.length; i++) {
            result[i] = proposals[proposalIds[i]];
        }

        return result;
    }

    function addProposalComment(
        uint256 proposalId, 
        string memory comment
    ) external onlySuperAdmin {
        require(proposals[proposalId].id != 0, "Proposal not found");

        proposalComments[proposalId].push(ProposalComment({
            commenter: msg.sender,
            comment: comment,
            timestamp: block.timestamp
        }));

        emit CommentAdded(proposalId, msg.sender, comment);
    }

    function getProposalComments(uint256 proposalId) 
        external 
        view 
        returns (ProposalComment[] memory) 
    {
        return proposalComments[proposalId];
    }

    function initializeDepartmentProposals(address department) 
        external 
        onlySuperAdmin 
    {
        require(
            departmentRegistry.departments(department).isActive,
            "Department not active"
        );
        departmentProposals[department] = new uint256[](0);
    }

    function getProposalMetrics() external view returns (
        uint256 total,
        uint256 approved,
        uint256 pending
    ) {
        return (totalProposals, approvedProposals, pendingProposals);
    }
}

